import React from 'react';
import { Typography, Box } from '@mui/material';
import { motion } from 'framer-motion';
import bg from '../../assets/myimage.png';
import './intro.css';

const Intro = () => {
  return (
    <section id='intro' className='intro'>
      <Box className='introContent'>

        {/* Animate text elements */}
        <motion.div
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, ease: 'easeOut' }}
        >
          <Typography variant="h1" className='introText'>
            NISHCHAY PATEL
          </Typography>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, delay: 0.5, ease: 'easeOut' }}
        >
          <Typography variant="h4" className='introTitle'>
            Software Engineer
          </Typography>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 1, ease: 'easeOut' }}
        >
          <Typography variant="body1" className='introPara' sx={{ fontSize: '1.2rem' }}>
            Bachelor of Science in Computer Science <br />
            Certification in Data Science <br /> <br />
            <strong>Georgia State University</strong>
          </Typography>
        </motion.div>

        {/* Animate button */}
        <motion.div
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, delay: 1.5, ease: 'easeOut' }}
          className='introButtonContainer'
        >
          <button className="btn">Contact Me</button>
        </motion.div>

      </Box>

      {/* Background image */}
      <motion.img
        initial={{ opacity: 0, x: '-100vw' }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 3, delay: 0.5, ease: 'easeOut' }}
        src={bg}
        alt="Profile"
        className='bg'
      />
    </section>
  );
};

export default Intro;
