import React from 'react';
import { Typography, Button, Box } from '@mui/material';
import { motion } from 'framer-motion';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';
import bg from '../../assets/myimage.png';
import './intro.css';

const Intro = () => {
  const particlesInit = async (main) => {
    await loadFull(main);
  };

  const particlesLoaded = (container) => {
    console.log(container);
  };

  return (
    <section id='intro' className='intro'>
      <Particles
        id="tsparticles"
        init={particlesInit}
        loaded={particlesLoaded}
        options={{
          background: {
            color: {
              value: "#000",
            },
          },
          fpsLimit: 60,
          interactivity: {
            events: {
              onClick: {
                enable: true,
                mode: "push",
              },
              onHover: {
                enable: true,
                mode: "repulse",
              },
              resize: true,
            },
            modes: {
              push: {
                quantity: 4,
              },
              repulse: {
                distance: 200,
                duration: 0.4,
              },
            },
          },
          particles: {
            color: {
              value: "#ffffff",
            },
            links: {
              color: "#ffffff",
              distance: 150,
              enable: true,
              opacity: 0.5,
              width: 1,
            },
            collisions: {
              enable: true,
            },
            move: {
              direction: "none",
              enable: true,
              outModes: {
                default: "bounce",
              },
              random: false,
              speed: 2,
              straight: false,
            },
            number: {
              density: {
                enable: true,
                area: 800,
              },
              value: 80,
            },
            opacity: {
              value: 0.5,
            },
            shape: {
              type: "circle",
            },
            size: {
              random: true,
              value: 5,
            },
          },
          detectRetina: true,
        }}
      />
      <Box className='introContent'>
        <motion.div
          initial={{ opacity: 0, y: '-100vh' }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, ease: 'backInOut' }}
        >
          <Typography variant="h1" className='introText'>
            NISHCHAY PATEL
          </Typography>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 3, delay: 3, ease: 'anticipate' }}
        >
          <Typography variant="h2" className='introTitle'>
            Software Engineer
          </Typography>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 3, delay: 0.9, ease: 'linear' }}
        >
          <Typography variant="body1" className='introPara'>
            Bachelor of Science in Computer Science <br />
            Certification in Data Science <br />
            <strong>Georgia State University</strong>
          </Typography>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, scale: 1.2 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 4, delay: 1, ease: 'easeInOut' }}
          className='introButtonContainer'
        >
          <Button
            variant="contained"
            color="primary"
            size="large"
            className='introButton'
            onClick={() => document.getElementById('contact').scrollIntoView({ behavior: 'smooth' })}
          >
            Contact Me
          </Button>
        </motion.div>
      </Box>
      <motion.img
        initial={{ opacity: 0, x: '-100vw' }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 5, delay: 1.5, ease: 'easeOut' }}
        src={bg}
        alt="Profile"
        className='bg'
      />
    </section>
  );
};

export default Intro;
