import React, { useState } from 'react';
import './navbar.css';
import logo from '../../assets/logo.png';
import { Link } from 'react-scroll';
import contactImg from '../../assets/contact.png';

const Navbar = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <nav className="navbar">
      <img src={logo} alt="Logo" className="logo" />

      <div className="desktopMenu">
        <Link activeClass="active" to="intro" spy={true} smooth={true} offset={-100} className="desktopMenuListItem">
          Home
        </Link>
        <a href="/Patel_Nishchay.pdf" target="_blank" rel="noopener noreferrer" className="desktopMenuListItem resumeLink">
          Resume
        </a>
        <Link activeClass="active" to="skills" spy={true} smooth={true} offset={-100} className="desktopMenuListItem">
          About
        </Link>
        <Link activeClass="active" to="works" spy={true} smooth={true} offset={-100} className="desktopMenuListItem">
          Experience
        </Link>
        <Link activeClass="active" to="projects" spy={true} smooth={true} offset={-100} className="desktopMenuListItem">
          Project
        </Link>
      </div>

      <div className="dropdown">
        <div className="dropdownToggle" onClick={toggleDropdown}>
          Certificates
        </div>
        <div className={`dropdownMenu ${isDropdownOpen ? 'active' : ''}`}>
          <a href="/Transcript_2024.pdf" target="_blank" rel="noopener noreferrer" className="dropdownMenuItem">
            Transcript
          </a>
          <a href="/AWS1.pdf" target="_blank" rel="noopener noreferrer" className="dropdownMenuItem">
            Amazon Web Services
          </a>
          <a href="/Nishchay Patel_Codepath.pdf" target="_blank" rel="noopener noreferrer" className="dropdownMenuItem">
            Codepath Web Development
          </a>
          <a href="/CodepathTIP.pdf" target="_blank" rel="noopener noreferrer" className="dropdownMenuItem">
            Codepath Technical Prep
          </a>
        </div>
      </div>

      <button className="desktopMenuBtn" onClick={() => document.getElementById('contact').scrollIntoView({ behavior: 'smooth' })}>
        <img src={contactImg} alt="" className="desktopMenuImg" /> Contact Me
      </button>
    </nav>
  );
};

export default Navbar;
