import React from 'react';
import { Typography, Button, Box } from '@mui/material';
import { motion } from 'framer-motion';
import bg from '../../assets/myimage.png';
import './intro.css';

const Intro = () => {
  return (
    <section id='intro' className='intro'>
      <Box className='introContent'>
        
        <motion.div
          initial={{ opacity: 0, y: '-100vh' }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, ease: 'backInOut' }}
        >
          <Typography variant="h1" className='introText'>
            NISHCHAY PATEL
          </Typography>
        </motion.div>
        
        <motion.div
          initial={{ opacity: 0, scale: 0 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 3, delay: 3, ease: 'anticipate' }}
        >
          <Typography variant="h2" className='introTitle'>
            Software Engineer
          </Typography>
        </motion.div>
        
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 3, delay: 1, ease: 'easeInOut' }}
        >
          <Typography variant="body1" className='introPara'>
            Bachelor of Science in Computer Science <br />
            Certification in Data Science <br />
            <strong>Georgia State University</strong>
          </Typography>
        </motion.div>
        
        <motion.div
          initial={{ opacity: 0, scale: 1.2 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 3, delay: 2, ease: 'easeInOut' }}
          className='introButtonContainer'
        >
          <Button
            variant="contained"
            color="primary"
            size="large"
            className='introButton'
            onClick={() => document.getElementById('contact').scrollIntoView({ behavior: 'smooth' })}
          >
            Contact Me
          </Button>
        </motion.div>
      
      </Box>
      
      <motion.img
        initial={{ opacity: 0, x: '-100vw' }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 5, delay: 1, ease: 'easeOut' }}
        src={bg}
        alt="Profile"
        className='bg'
      />
    
    </section>
  );
};

export default Intro;
