import React from 'react';
import './intro.css';
import bg from '../../assets/myimage.png';
import { motion } from 'framer-motion';

const Intro = () => {
  return (
    <section id='intro' className='intro'>
      <div className='introContent'>
        <motion.h1
          initial={{ opacity: 0, y: '-100vh' }} // Start from off-screen
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, ease: 'backInOut' }} // Bouncy ease
          className='introText'
        >
          NISHCHAY PATEL
        </motion.h1>
        <motion.h2
          initial={{ opacity: 0, scale: 0 }} // Start shrunken
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 3, delay: 3, ease: 'anticipate' }} // Anticipatory ease
          className='introTitle'
        >
          Software Engineer
        </motion.h2>
        <motion.p
          initial={{ opacity: 0, y: 50 }} // Slide in from below
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 3, delay: 0.9, ease: 'linear' }} // Linear ease
          className='introPara'
        >
          Bachelor of Science in Computer Science <br />
          Certification in Data Science <br />
          <strong>Georgia State University</strong>
        </motion.p>
        <motion.div
          initial={{ opacity: 0, scale: 1.2 }} // Start slightly zoomed
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 4, delay: 1, ease: 'easeInOut' }} // Standard ease
          className='introButtonContainer'
        >
          {/* Add your button content here */}
        </motion.div>
      </div>
      <motion.img
        initial={{ opacity: 0, x: '-100vw' }} // Start off-screen to the left
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 5, delay: 1.5, ease: 'easeOut' }} // Smoother reveal
        src={bg}
        alt="Profile"
        className='bg'
      />
    </section>
  );
};

export default Intro;
