import React, { useEffect } from 'react';
import { Typography, Box } from '@mui/material';
import { motion, useAnimation } from 'framer-motion';
import { useViewportScroll } from 'framer-motion';
import bg from '../../assets/myimage.png';
import './intro.css';

const Intro = () => {
  const controls = useAnimation();
  const { scrollYProgress } = useViewportScroll();

  useEffect(() => {
    const sequence = async () => {
      await controls.start({
        opacity: 1,
        y: 0,
        transition: { duration: 1, ease: 'backInOut' }
      });
      await controls.start({
        scale: 1,
        transition: { duration: 3, ease: 'anticipate' }
      });
      await controls.start({
        opacity: 1,
        y: 0,
        transition: { duration: 3, delay: 0.9, ease: 'linear' }
      });
      await controls.start({
        scale: 1,
        transition: { duration: 4, delay: 1, ease: 'easeInOut' }
      });
    };

    sequence();
  }, [controls]);

  return (
    <section id='intro' className='intro'>
      <Box className='introContent'>

        {/* Name */}
        <motion.div
          initial={{ opacity: 0, y: '-100vh' }}
          animate={controls}
        >
          <Typography variant="h1" className='introText'>
            NISHCHAY PATEL
            <br/><br/>
          </Typography>
        </motion.div>

        {/* Title */}
        <motion.div
          initial={{ opacity: 0, scale: 0 }}
          animate={controls}
        >
          <Typography variant="h2" className='introTitle'>
            Software Engineer
            <br/><br/>
          </Typography>
        </motion.div>

        {/* Education */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={controls}
        >
          <Typography variant="body1" className='introPara' sx={{ fontSize: '1.8rem' }}>
            Bachelor of Science in Computer Science <br />
            Certification in Data Science <br /><br />
            <strong>Georgia State University</strong>
          </Typography>
        </motion.div>

        {/* Button Container */}
        <motion.div
          initial={{ opacity: 0, scale: 1.2 }}
          animate={controls}
          className='introButtonContainer'
        >
          {/* Placeholder for Button */}
        </motion.div>

      </Box>

      {/* Background Image */}
      <motion.img
        initial={{ opacity: 0, x: '-100vw' }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 5, delay: 1.5, ease: 'easeOut' }}
        src={bg}
        alt="Profile"
        className='bg'
      />

    </section>
  );
};

export default Intro;
